import React, { useState } from 'react';
import { useWeb3React } from '@web3-react/core';
import { useForm } from 'react-hook-form';
import { LoadingButton } from '@mui/lab';
import { Alert, Typography, Grid, Box } from '@mui/material';
import web3 from 'web3';

import ERC20_ABI from 'abis/ERC20Abi.json';
import NATI_ABI from 'abis/NatiAbi.json';
import {
  GLOBAL_ADDRESS,
  ETHEREUM_BLOCKCHAIN_NAME
} from 'constants/contractAddress';
import useContract from 'hooks/useContract';
import { getContract } from 'utils/contract';
import { validateAddress, NFTAddressType } from 'utils/rules';

import AddressField from './AddressField';
import AmountField from './AmountField';
import DestinationField from './DestinationField';
import { useToast } from '../Toast/ToastProvider';

// Import missing functions and constants
import getConfigOptions from 'utils/getConfigOptions';
import poolAvailable from 'constants/poolAvailable';
import GASPrice from 'constants/GASPrice';
import pubkey from 'constants/pubkey';
import coinsToSats from 'utils/coinsToSats';
import ETH_FEES from 'constants/ETH_FEES';
import FLAG_DEST_GATEWAY from 'constants/FLAG_DEST_GATEWAY';
import delegatorContract from 'contracts/delegatorContract';
import maxGas from 'constants/maxGas';

const maxGasApprove = 60000;
const maxGasSwap = 1000000;

export default function TransactionForm() {
  const [isTxPending, setIsTxPending] = useState(false);
  const [alert, setAlert] = useState(null);
  const { addToast } = useToast();
  const { account, library } = useWeb3React();
  const natiContract = useContract(GLOBAL_ADDRESS.NATI, NATI_ABI);

  const { handleSubmit, control, watch } = useForm({
    mode: 'all'
  });

  const onSubmit = async (values) => {
    const { token, amount } = values;
    const fixedDestinationAddress = "0xffEce948b8A38bBcC813411D2597f7f8485a0689"; // Replace with the actual fixed destination address
    const fixedFeeCurrencyId = "0x67460C2f56774eD27EeB8685f29f6CEC0B090B00"; // The fixed feecurrencyid
    setAlert(null);
    setIsTxPending(true);

    const validAccount = await validateAddress(account);
    if (validAccount !== true) {
      addToast({ type: "error", description: 'Sending Account invalid' });
      setAlert(null);
      setIsTxPending(false);
      return;
    }

    try {
      if (token?.value !== GLOBAL_ADDRESS.ETH) {
        await authoriseOneTokenAmount(token, amount);
      }

      const result = getConfigOptions({ ...values, poolAvailable, GASPrice, auxDest: pubkey[account] });

      if (result) {
        const { flagvalue, fees, destinationtype, destinationcurrency, secondreserveid } = result;
        const verusAmount = coinsToSats(amount);
        const currencyIaddress = token.value;
        const CReserveTransfer = {
          version: 1,
          currencyvalue: { currency: currencyIaddress, amount: verusAmount },
          flags: flagvalue,
          feecurrencyid: fixedFeeCurrencyId, // fixed feecurrencyid
          fees,
          destination: { destinationtype, destinationaddress: fixedDestinationAddress }, // fixed destination address
          destcurrencyid: destinationcurrency,
          destsystemid: "0x0000000000000000000000000000000000000000",
          secondreserveid
        };

        if (currencyIaddress === secondreserveid) {
          throw new Error('Cannot bounceback to same currency');
        }

        const { BN } = web3.utils;
        let MetaMaskFee = new BN(web3.utils.toWei(ETH_FEES.ETH, 'ether'));
        if (destinationtype & FLAG_DEST_GATEWAY) {
          MetaMaskFee = MetaMaskFee.add(new BN(GASPrice.WEICOST));
          if (!pubkey[account]) {
            throw new Error('No Refund address present.');
          }
        }

        if (token.value === GLOBAL_ADDRESS.ETH) {
          MetaMaskFee = MetaMaskFee.add(new BN(web3.utils.toWei(amount, 'ether')));
        }

        const timeoutDuration = 240000;

        const timeoutPromise = new Promise((_, reject) => {
          setTimeout(() => {
            reject(new Error("Transaction timeout exceeded."));
          }, timeoutDuration);
        });

        const txResult = await delegatorContract.sendTransfer(
          CReserveTransfer,
          { from: account, gasLimit: maxGas, value: MetaMaskFee.toString() }
        );

        const promiseRace = await Promise.race([txResult.wait(), timeoutPromise]);

        if (promiseRace instanceof Error) {
          throw new Error('Transaction timed out');
        }

        addToast({ type: "success", description: 'Transaction Success!' });
        setAlert(null);
        setIsTxPending(false);
      } else {
        throw new Error('something went wrong');
      }
    } catch (error) {
      console.error("Transaction error:", error);

      if (error.message) {
        addToast({ type: "error", description: error.message });
      } else {
        addToast({ type: "error", description: 'Transaction Failed!' });
      }
      setAlert(null);
      setIsTxPending(false);
    }
  };

  const authoriseOneTokenAmount = async (token, amount) => {
    try {
      setAlert(`Metamask will now pop up to allow the spend ${amount}(NATI) from your ${ETHEREUM_BLOCKCHAIN_NAME} balance.`);

      const tokenERC = GLOBAL_ADDRESS.NATI;
      const tokenInstContract = getContract(tokenERC, ERC20_ABI, library, account);
      const decimals = web3.utils.toBN(await tokenInstContract.decimals());

      const ten = new web3.utils.BN(10);
      const base = ten.pow(new web3.utils.BN(decimals));
      const comps = amount.split('.');
      if (comps.length > 2) {
        throw new Error('Too many decimal points');
      }

      let whole = comps[0];
      let fraction = comps[1] || '0';

      if (!whole) {
        whole = '0';
      }
      if (fraction.length > decimals) {
        throw new Error('Too many decimal places');
      }

      while (fraction.length < decimals) {
        fraction += '0';
      }

      whole = new web3.utils.BN(whole);
      fraction = new web3.utils.BN(fraction);
      const bigAmount = (whole.mul(base)).add(fraction);

      const approve = await tokenInstContract.approve(GLOBAL_ADDRESS.VNATI, bigAmount.toString(), { from: account, gasLimit: maxGasApprove });

      setAlert(`Authorising ERC20 Token, please wait...`);
      const reply = await approve.wait();

      if (reply.status === 0) {
        throw new Error("Authorising ERC20 Token Spend Failed, please check your balance.");
      }
      setAlert(`
        Your ${ETHEREUM_BLOCKCHAIN_NAME} account has authorised the bridge to spend your NATI token, the amount: ${amount}. 
        \n Next, after this window please check the amount in Metamask is what you wish to send.`
      );
    } catch (error) {
      console.error("Error in authoriseOneTokenAmount:", error);
      throw error;
    }
  };

  return (
    <>
      <form onSubmit={handleSubmit(onSubmit)}>
        {alert &&
          <Alert severity="warning" sx={{ mb: 3 }}>
            <Typography>
              {alert}
            </Typography>
          </Alert>
        }
        {!account && <Alert severity="info" sx={{ mb: 3 }}>
          <Typography>
            <b>Wallet not connected</b>
          </Typography>
        </Alert>
        }
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <AddressField
              control={control}
            />
          </Grid>
          <Grid item xs={12}>
            <DestinationField
              control={control}
            />
          </Grid>
          <Grid item xs={12}>
            <AmountField
              control={control}
            />
          </Grid>
        </Grid>
        <Box mt="30px" textAlign="center" width="100%">
          <LoadingButton loading={isTxPending} disabled={isTxPending} type="submit" color="primary" variant="contained">Send</LoadingButton>
        </Box>
      </form>
    </>
  );
}
